CREATE VIEW INTEGRACAO_CDNET_SALES_ITEMS AS
SELECT LTRIM(RTRIM(D2_FILIAL)) AS EMPRESA,LTRIM(RTRIM(D2_DOC)) AS DOCUMENTO,LTRIM(RTRIM(D2_SERIE)) AS SERIE,
LTRIM(RTRIM(D2_COD)) AS CODIGO_PRODUTO,D2_QUANT AS QUANTIDADE, D2_PRCVEN AS PRECO_VENDA, D2_TOTAL AS TOTAL,D2.R_E_C_N_O_ AS REC FROM SD2010 (NOLOCK) D2
WHERE D2.D_E_L_E_T_ = '' AND D2_LOCAL IN ('01','02','03','04','10') AND
 LEFT(D2_CF,4) IN ('5101','5102','5103','5104','5105','5106','5109','5110','5111','5112','5113','5114','5115','5116','5117','5118','5119','5120','5122','5123','5124','5125','5251','5252','5253','5254','5255','5256','5257','5258','5301','5302','5303','5304','5305','5306','5307','5351','5352','5353','5354','5355','5356','5357', 
'5401','5402','5403','5405','5651','5652','5653','5654','5655','5656','6101','6102','6103','6104','6105','6106','6107','6108','6109','6110','6111','6112','6113','6114','6115','6116','6117','6118','6119','6120','6122','6123','6124','6251','6252','6253','6254','6255','6256','6257','6258','6301','6302','6303','6304','6305','6306','6307','6351','6352',  
'6353','6354','6355','6356','6357','6401','6402','6403','6404','6651','6652','6653','6654','6655','6656','7101','7102','7105','7106','7127','7251','7301','7358','7501','7651','7654')  
AND SUBSTRING(D2_COD,2,1) NOT IN ('9') AND SUBSTRING(D2_COD,1,1) NOT IN ('0','6','9')